package com.example.finalsetcalendar;

import java.util.Dictionary;
import java.util.Hashtable;

public class CannyClassifier {

    public Hashtable<String, String> mlbp_dict;
    public String roi_mlbp;
    public double sim;

    // Constructor
    public CannyClassifier() {
        //this.roi_mlbp = roi_mlbp;
        //this.mlbp_dict = new Dictionary<String, String>();
        this.mlbp_dict = new Hashtable();
        this.mlbp_dict.put("M", "");
        this.mlbp_dict.put("6", "");
    }

    public double compare_mlbp(String mlbp_1, String mlbp_2){
        if (mlbp_1 == null || mlbp_2 == null){
            return -1.0;
        }
        if (mlbp_1.length() != mlbp_2.length()){
            return -1.0;
        }

        int dist = 0;
        for (int i=0; i<mlbp_1.length(); i++){
            if (mlbp_1.charAt(i) != mlbp_2.charAt(i)){
                dist += 1;
            }
        }
        return 1.0 - ((double)dist / (double)mlbp_1.length());
    }

}
